    /**
     * Invia l'email contenente il link per il reset della password.
     * Questo metodo viene chiamato dal RegisterController.
     */
    public void inviaEmailResetPassword(String nomeSocio, String emailSocio, String resetLink, String username) {
        
        System.out.println("INFO: Preparazione email di reset password per " + emailSocio + "...");

        try {
            MimeMessage mimeMessage = emailSender.createMimeMessage();
            MimeMessageHelper helper = new MimeMessageHelper(mimeMessage, "utf-8");

            // 1. Costruire Oggetto e Corpo del messaggio
            String oggetto = "Richiesta di reset password - Casa di Davide";
            String corpoEmail = costruisciCorpoEmailReset(nomeSocio, resetLink, username);

            // 2. Impostare i dettagli del messaggio
            helper.setText(corpoEmail, true); // true indica che il corpo è HTML
            helper.setTo(emailSocio);
            helper.setSubject(oggetto);
            helper.setFrom(fromEmail, FROM_NAME);

            // 3. Inviare il messaggio
            emailSender.send(mimeMessage);

            System.out.println("SUCCESS: Email di reset password inviata a " + emailSocio);

        } catch (Exception e) {
            System.err.println("ERRORE GRAVE: Impossibile inviare email di reset a " 
                               + emailSocio + ". Errore: " + e.getMessage());
            e.printStackTrace();
        }
    }


    /**
     * Costruisce il corpo HTML per l'email di reset password.
     */
    private String costruisciCorpoEmailReset(String nomeSocio, String resetLink, String username) {
        
        // Testo del Preheader
        String preheaderText = "Recupera le tue credenziali.";

        return "<html>"
            + "<body style='font-family: Arial, sans-serif;'>"

            // Blocco Preheader Nascosto
            + "<div style='display:none; max-height:0px; overflow:hidden;'>"
            + preheaderText
            + filler
            + "</div>"
            
            + "<h2>Ciao " + nomeSocio + ",</h2>"
            + "<p>Abbiamo ricevuto una richiesta di recupero delle credenziali per il tuo account Casa di Davide.</p>"
            + "<p>Se non sei stato tu a fare questa richiesta, puoi tranquillamente ignorare questa email.</p>"
            + "<p>Il tuo <strong>nome utente</strong> per accedere è: <strong>" + username + "</strong></p>"
            + "<p>Se hai dimenticato anche la <strong>password</strong>, clicca sul pulsante qui sotto per sceglierne una nuova. Se invece ora ricordi la password, puoi ignorare il resto di questa email.</p>"
            + "<br>"
            
            // Pulsante
            + "<a href='" + resetLink + "' "
            + "   style='background-color: #0d6efd; color: white; padding: 12px 20px; text-align: center; text-decoration: none; display: inline-block; border-radius: 5px; font-weight: bold;'>"
            + " Reimposta la tua Password"
            + "</a>"
            + "<br><br>"
            
            + "<p>Se il pulsante non funziona, puoi copiare e incollare il seguente link nel tuo browser:</p>"
            + "<p style='font-size: 12px; color: #555;'>" + resetLink + "</p>"
            + "<br>"
            + "<p>Grazie,<br>Fondazione Casa di Davide</p>"
            + "</body>"
            + "</html>";
    }
    
